<!DOCTYPE html>
<html>
  <head>
    <title>tinyTimer</title>
    <meta charset="utf-8">
    <link href='http://fonts.googleapis.com/css?family=Denk+One' rel='stylesheet' type='text/css'>
    <style>
      #counter {
        font-family: 'Denk One', sans-serif;
        font-size:300px;
        line-height: 300px;
        position:absolute;
        top:50%;
        left:50%;
        margin-left:-600px;
        width:1200px;
        margin-top:-150px;
        height:300px;
        text-align:center;
      }

      body.on {background-color:#000;}
      body.on #counter {color:#fff;}
      body.off {background-color:#fff;}
      body.off #counter {color:#000;}

      body.on #logo, body.on #logo2 {display:none;}

      #logo span {display: none;}
      #logo:hover {opacity: 1;}
      #logo {
        background-image: url('data:image/png;base64,');
        background-size: contain;
        background-position: bottom center;
        background-repeat: no-repeat;
        display: block;
        width:100px;
        height:100px;
        position:absolute;
        right:20px;
        bottom:20px;
        opacity:0.3;
      }
      #logo2 span {display: none;}
      #logo2:hover {opacity: 1;}
      #logo2 {
        background-image: url('data:image/png;base64,');
        background-size: contain;
        background-position: bottom center;
        background-repeat: no-repeat;
        display: block;
        width:100px;
        height:100px;
        position:absolute;
        left:20px;
        bottom:20px;
        opacity:0.3;
      }
    </style>
  </head>
  <body>
    <div id="counter" data-left="<?php echo isset($_GET['m'])?($_GET['m']*60):120; ?>"></div>
    <a id="logo" href="http://ecosim.lt"><span>EcoSim</span></a>
    <a id="logo2" href="http://studio.tiny.lt"><span>tinyStudio</span></a>
    <script src="//ajax.googleapis.com/ajax/libs/jquery/1.8.3/jquery.min.js"></script>
    <script>
      jQuery(document).ready(function(){
        showIt();
        jQuery(document).click(function(){
          jQuery('body').addClass('off').removeClass('on');
          jQuery('#counter').attr('data-left','<?php echo isset($_GET['m'])?($_GET['m']*60):120; ?>');
          showIt();
        });
        setInterval('decrease()',1000);
      });
      function decrease() {
          if (jQuery('#counter').attr('data-left')>0) {
            jQuery('#counter').attr('data-left',jQuery('#counter').attr('data-left')-1);
            showIt();
          } else {
            blinkIt();
          }
      }
      function blinkIt() {
        if (jQuery('body').hasClass('off')) {
          jQuery('body').addClass('on').removeClass('off');
        } else {
          jQuery('body').addClass('off').removeClass('on');
        }
      }
      function showIt() {
        var n = jQuery('#counter').attr('data-left')*1;
        if (n>1440*60) {
          n = 1440*60;
          jQuery('#counter').attr('data-left',1440*60);
        }
        if (n<1 && n!=0) {
          n = 1;
          jQuery('#counter').attr('data-left',1);
        }
        var h = Math.floor(n/(60*60));
        n = n % (60*60);
        console.log(n);
        if (h>0)
          var h_text = h+":";//(h<10?'0':'')+h+':';
        else 
          var h_text = '';
        var m = Math.floor(n/60);
        var s = n%60;
        var text = h_text+(m<10?'0':'')+m+':'+(s<10?'0':'')+s;
        jQuery('#counter').html(text);
      }
    </script>
  </body>
</html>